package shopping.external;

public class PointAccumulation {

    private String name;
    private Money points;

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public Money getPoints() {
        return points;
    }

    public void setPoints(Money points) {
        this.points = points;
    }
}
