mapper.insert:
INSERT INTO vehicle (
                연식,
                배기량,
                maintenanceHistories
    ) 
VALUES (
                #{ 연식 },
                #{ 배기량 },
                #{ maintenanceHistories }
    )

mapper.update:
UPDATE reservation
SET 

    연식 = #{ 연식 },

    배기량 = #{ 배기량 },

    maintenanceHistories = #{ maintenanceHistories }
WHERE id = #{id}
